(1, 'en', 'WPML', 'Text for alternative languages for posts', 'This post is also available in: %s', NULL, NULL, '', 'LINE', NULL, 0, '', 'c9203e3110feb389a62b2f341a6c346a', 'optional', NULL),
(2, 'en', 'WP', 'Blog Title', 'mama_medicine', NULL, NULL, '', 'LINE', NULL, 0, '', '5ff0887b4c486c0329311e8d30610d58', 'optional', NULL),
(3, 'en', 'gutenberg-2', '56f657850b998d66a24eaf1c912d3495', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:', 1, 1, '', 'AREA', 'core/paragraph', 10, '', 'ccb40be280c5ff37205b8d92229fee08', 'optional', 50),
(4, 'en', 'gutenberg-2', 'e66285028ba3c394645459b7cd15e047', 'Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like piña coladas. (And gettin\' caught in the rain.)', 1, 2, '', 'AREA', 'core/quote', 10, '', '75444e359e526dd8e0b03ac9352ba15f', 'optional', 40),
(5, 'en', 'gutenberg-2', '66415f712eacd1df62af7332979a6b8a', '...or something like this:', 1, 3, '', 'LINE', 'core/paragraph', 10, '', 'ecaf2221631d619c531d8cef2d97dc60', 'optional', 4),
(6, 'en', 'gutenberg-2', '79c202a201cfc85c89f0cd7eb7e7114a', 'The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.', 1, 4, '', 'AREA', 'core/quote', 10, '', 'c627133af8d5b919f1219f34b0ab86c6', 'optional', 40),
(7, 'en', 'gutenberg-2', '5cd6c435f0d41d8089a919a57c1596f2', 'As a new WordPress user, you should go to <a href=\"http://localhost/mama_medicine/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 1, 5, '', 'VISUAL', 'core/paragraph', 10, '', '2f88e92730c52d45da3b7cd2db6fda9c', 'optional', 24),
(8, 'en', 'gutenberg-1', '66f706cb6c29075c85562dff4a379f5f', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 2, 1, '', 'AREA', 'core/paragraph', 10, '', '96ffd1d24a653a29b93be1f2168c17c7', 'optional', 15),
(9, 'en', 'gutenberg-3', 'b74b235bcbf0e7c49291a61f2d50efe0', 'Who we are', 3, 1, 'h2', 'LINE', 'core/heading', 10, '', '0c01ba0a680c4d039375e15032ecafda', 'optional', 3),
(10, 'en', 'gutenberg-3', '80617af2f3e1bf66722230a7ba5fa9ef', '<strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Our website address is: http://localhost/mama_medicine.', 3, 2, '', 'VISUAL', 'core/paragraph', 10, '', '242d7b2c5c9b8e29c23df9c3df5b5ba4', 'optional', 6),
