INSERT INTO `wp_icl_string_translations` (`id`, `string_id`, `language`, `status`, `value`, `mo_string`, `translator_id`, `translation_service`, `batch_id`, `translation_date`) VALUES
(1, 3, 'nl', 10, 'Dit is een voorbeeldpagina.\nZe verschilt van een blogbericht omdat ze op één plaats blijft en in de navigatie van je site verschijnt (in de meeste thema\'s).\nDe meeste mensen beginnen met een Over pagina die hen voorstelt aan potentiële sitebezoekers.\nHet zou zoiets kunnen zeggen als dit:   ', NULL, NULL, 'local', 0, '2024-08-29 08:16:30'),
(2, 4, 'nl', 10, 'Hallo!\nIk ben overdag fietskoerier, \'s nachts aspirant-acteur en dit is mijn website.\nIk woon in Los Angeles, heb een geweldige hond die Jack heet en ik hou van piña coladas.\n(En verstrikt raken in de regen.)   ', NULL, NULL, 'local', 0, '2024-08-29 08:16:30'),
(3, 5, 'nl', 10, '...of zoiets:', NULL, NULL, 'local', 0, '2024-08-29 08:16:30'),
(4, 6, 'nl', 10, 'De XYZ Doohickey Company werd opgericht in 1971 en heeft sindsdien doohickeys van hoge kwaliteit geleverd aan het publiek.\nXYZ is gevestigd in Gotham City, heeft meer dan 2000 mensen in dienst en doet allerlei geweldige dingen voor de Gotham-gemeenschap. ', NULL, NULL, 'local', 0, '2024-08-29 08:16:30'),
(5, 7, 'nl', 10, 'Als nieuwe WordPress gebruiker moet je naar <a href=\"http://localhost/mama_medicine/wp-admin/\">je dashboard</a> gaan om deze pagina te verwijderen en nieuwe pagina\'s te maken voor je inhoud.\nVeel plezier! ', NULL, NULL, 'local', 0, '2024-08-29 08:16:30'),
(6, 8, 'nl', 10, 'Welkom bij WordPress.\nDit is je eerste bericht.\nBewerk of verwijder het, en begin dan met schrijven!  ', NULL, NULL, 'local', 0, '2024-08-29 07:05:02'),
(7, 9, 'nl', 10, 'Wie we zijn', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(8, 10, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Ons website adres is: http://localhost/mama_medicine.', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(9, 11, 'nl', 10, 'Reacties', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(10, 12, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Wanneer bezoekers reacties achterlaten op de site, verzamelen we de gegevens die worden getoond in het reactieformulier, en ook het IP-adres en de browser user agent string van de bezoeker om spam te helpen opsporen.', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(11, 13, 'nl', 10, 'Een geanonimiseerde tekenreeks die is gemaakt van uw e-mailadres (ook wel een hash genoemd) kan aan de Gravatar-service worden verstrekt om te zien of u deze gebruikt.\nHet privacybeleid van de Gravatar-service is hier beschikbaar: https://automattic.com/privacy/. Na goedkeuring van je commentaar is je profielfoto zichtbaar voor het publiek in de context van je commentaar. ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(12, 14, 'nl', 10, 'Media', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(13, 15, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Als u afbeeldingen uploadt naar de website, moet u het uploaden van afbeeldingen met ingebedde locatiegegevens (EXIF GPS) vermijden.\nBezoekers van de website kunnen alle locatiegegevens van afbeeldingen op de website downloaden en extraheren. ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(14, 16, 'nl', 10, 'Cookies', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(15, 17, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Als u een reactie achterlaat op onze site kunt u ervoor kiezen om uw naam, e-mailadres en website op te slaan in cookies.\nDeze zijn voor uw gemak zodat u uw gegevens niet opnieuw hoeft in te vullen als u een andere reactie achterlaat.\nDeze cookies gaan een jaar mee.  ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(16, 18, 'nl', 10, 'Als u onze inlogpagina bezoekt, plaatsen we een tijdelijke cookie om te bepalen of uw browser cookies accepteert.\nDeze cookie bevat geen persoonlijke gegevens en wordt verwijderd wanneer u uw browser afsluit. ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(17, 19, 'nl', 10, 'Wanneer je inlogt, zullen we ook een aantal cookies plaatsen om je inloggegevens en schermkeuzes op te slaan.\nDe aanmeldingscookies blijven twee dagen bewaard en de cookies voor schermopties blijven een jaar bewaard.\nAls u \"Onthoud mij\" selecteert, blijft uw aanmelding twee weken bewaard.\nAls u zich afmeldt bij uw account, worden de aanmeldingscookies verwijderd.   ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(18, 20, 'nl', 10, 'Als je een artikel bewerkt of publiceert, wordt er een extra cookie opgeslagen in je browser.\nDeze cookie bevat geen persoonlijke gegevens en geeft alleen de post-ID weer van het artikel dat je zojuist hebt bewerkt.\nHet vervalt na 1 dag.  ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(19, 21, 'nl', 10, 'Ingebedde inhoud van andere websites', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(20, 22, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Artikelen op deze site kunnen ingesloten inhoud bevatten (bijv. video\'s, afbeeldingen, artikelen, enz.).\nIngesloten inhoud van andere websites gedraagt zich op exact dezelfde manier alsof de bezoeker de andere website heeft bezocht. ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(21, 23, 'nl', 10, 'Deze websites kunnen gegevens over u verzamelen, cookies gebruiken, extra tracking van derden insluiten en uw interactie met die ingesloten inhoud bijhouden, inclusief het bijhouden van uw interactie met de ingesloten inhoud als u een account hebt en bent aangemeld bij die website.', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(22, 24, 'nl', 10, 'Met wie we uw gegevens delen', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(23, 25, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Als u een wachtwoord-reset aanvraagt, wordt uw IP-adres opgenomen in de e-mail met de reset.', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(24, 26, 'nl', 10, 'Hoe lang we uw gegevens bewaren', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(25, 27, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Als je een opmerking achterlaat, worden de opmerking en de metagegevens voor onbepaalde tijd bewaard.\nDit is zodat we vervolgcommentaren automatisch kunnen herkennen en goedkeuren in plaats van ze vast te houden in een moderatiewachtrij. ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(26, 28, 'nl', 10, 'Voor gebruikers die zich op onze website registreren (indien van toepassing), slaan we ook de persoonlijke informatie op die ze in hun gebruikersprofiel verstrekken.\nAlle gebruikers kunnen hun persoonlijke informatie op elk moment inzien, bewerken of verwijderen (behalve dat ze hun gebruikersnaam niet kunnen wijzigen).\nWebsitebeheerders kunnen deze informatie ook zien en bewerken.  ', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(27, 29, 'nl', 10, 'Welke rechten heb je over je gegevens?', NULL, NULL, 'local', 0, '2024-08-29 09:26:51'),
(28, 30, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Als u een account hebt op deze site, of als u opmerkingen hebt achtergelaten, kunt u verzoeken om een geëxporteerd bestand te ontvangen van de persoonlijke gegevens die wij over u hebben, inclusief alle gegevens die u aan ons hebt verstrekt.\nU kunt ook verzoeken dat wij uw persoonlijke gegevens wissen.\nDit geldt niet voor gegevens die we verplicht moeten bewaren voor administratieve, juridische of veiligheidsdoeleinden.  ', NULL, NULL, 'local', 0, '2024-08-29 09:26:52'),
(29, 31, 'nl', 10, 'Waar uw gegevens naartoe worden verzonden', NULL, NULL, 'local', 0, '2024-08-29 09:26:52'),
(30, 32, 'nl', 10, '<strong class=\"privacy-policy-tutorial\">Voorgestelde tekst: </strong>Reacties van bezoekers kunnen worden gecontroleerd door een geautomatiseerde spamdetectieservice.', NULL, NULL, 'local', 0, '2024-08-29 09:26:52'),
